MainGame:
	# set the number of lives the player starts with
	addi $t0, $zero, 3
	sb $t0, numberOfLives
	
MainGameLoop:
	lb $t0, runGame  # load the value for run game 
	bne $t0, 1, EndGame  # if runGame is false, end the game, otherwise continue running
	
	# Draw the game
	jal DrawGame
	jal Sleep
EndGame:
	j Exit

DrawGame:
	sw $ra, 0($sp)  # save the current current address to the stack
	# draw the different regions (water + road + goal)
	jal DrawWater
	jal DrawRoad
	jal DrawGoalRegion
	
	# draw safe zones (spawn and middle)
	addi $sp, $sp, -4 
	lw $t1, spawnPos  # load the spawnPos into $t1
	sw $t1, 0($sp)  # pass the spawnPos to DrawSafeZone
	jal DrawSafeZone
	
	addi $sp, $sp, -4
	lw $t1, safeZonePos  # load the safeZonePos into $t1
	sw $t1, 0($sp)  # pass the safeZonePos to DrawSafeZone
	jal DrawSafeZone
	
	# draw the logs in the first row
	lw $t0, row1LogPosX
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row1LogPosY
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	jal DrawShortLog
	
	# draw the turtles in the second row
	lw $t0, row2TurtlePosX
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row2TurtlePosY
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	jal DrawTurtle
	
	# draw the logs in the third row
	lw $t0, row4LogPosX
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row4LogPosY
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	jal DrawShortLog
	
	# draw the logs in the fourth row
	jal DrawRow3Log
	
	# draw the turtles in the fifth row
	lw $t0, row5TurtlePosX
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row5TurtlePosY
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	jal DrawTurtle
	
	# draw the third car row
	la $t0, pinkCar
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row8CarPosX
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	lw $t0, row8CarPosY
	addi $sp, $sp, -4
	sw $t0, 0($sp)
	jal DrawCar
	
	
	jal DrawFrog
	
	lw $ra, 0($sp)  # load the current return address
	jr $ra
	

DrawCar:
	lw $s2, 0($sp)  # load the y position value into $s2
	addi $sp, $sp, 4
	
	lw $s1, 0($sp)  # load the x position value into $s1
	addi $sp, $sp, 4
	
	lw $s3, 0($sp)  # load the sprite into $s3
	sw $ra, 0($sp)  # store the current $ra value overriting the sprite data
	add $s0, $zero, $zero  # initialize the x offset to zero
DrawCarLoop:
	beq $s0, 504, EndCarLoop  # if the loop has iterated three times, end it
	
	# pass the sprite into the function
	addi $sp, $sp, -4 
	sw $s3, 0($sp) 
	
	# pass the beginning pixel into the function
	add $t0, $zero, $s1  # load the x position of the log into $t0
	add $t0, $t0, $s0  # add the x offset to the x position
	
	blt $t0, 512, StoreFirstCarPixelPosition  # if the x position is greater than 512 due to the offset
	addi $t0, $t0, -512  # subtract 512 from it, that is, $t0 = $t0 % 512
StoreFirstCarPixelPosition:
	add $t1, $zero, $s2  # load the y position of the frog into $t1
	# calculate the beginning pixel of the sprite
	add $t1, $t1, $t0
	la $t2, 0($gp)  # load the display address
	add $t1, $t1, $t2  # add the pixel offset to the display address
	addi $sp, $sp, -4 
	sw $t1, 0($sp)  # store the beginning pixel in the stack
	
	# pass the area of the sprite
	addi $t0, $zero, 4096
	addi $sp, $sp, -4 
	sw $t0, 0($sp)
	
	# pass the row width of the sprite
	addu $t0, $zero, 32
	addi $sp, $sp, -4
	sw $t0, 0($sp)

	jal DrawSprite
	
	addi $s0, $s0, 168  # increment the x offset for the next log
	j DrawCarLoop
EndCarLoop:	
	# load the current $ra
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	jr $ra
	
	
DrawShortLog:
	lw $s2, 0($sp)  # load the y position value into $s2
	addi $sp, $sp, 4
	
	lw $s1, 0($sp)  # load the x position value into $s1
	addi $sp, $sp, 4
	
	addi $sp, $sp, -4
	sw $ra, 0($sp)  # store the current $ra value
	add $s0, $zero, $zero  # initialize the x offset to zero
DrawShortLogLoop:
	beq $s0, 504, EndShortLogLoop  # if the loop has iterated three times, end it
	
	# pass the sprite into the function
	addi $sp, $sp, -4 
	la $t1, shortLog
	sw $t1, 0($sp) 
	
	# pass the beginning pixel into the function
	add $t0, $zero, $s1  # load the x position of the log into $t0
	add $t0, $t0, $s0  # add the x offset to the x position
	
	blt $t0, 512, StoreFirstShortLogPixelPosition  # if the x position is greater than 512 due to the offset
	addi $t0, $t0, -512  # subtract 512 from it, that is, $t0 = $t0 % 512
StoreFirstShortLogPixelPosition:
	add $t1, $zero, $s2  # load the y position of the frog into $t1
	# calculate the beginning pixel of the sprite
	add $t1, $t1, $t0
	la $t2, 0($gp)  # load the display address
	add $t1, $t1, $t2  # add the pixel offset to the display address
	addi $sp, $sp, -4 
	sw $t1, 0($sp)  # store the beginning pixel in the stack
	
	# pass the area of the sprite
	addi $t0, $zero, 4096
	addi $sp, $sp, -4 
	sw $t0, 0($sp)
	
	# pass the row width of the sprite
	addu $t0, $zero, 128
	addi $sp, $sp, -4
	sw $t0, 0($sp)

	jal DrawSprite
	
	addi $s0, $s0, 168  # increment the x offset for the next log
	j DrawShortLogLoop
EndShortLogLoop:	
	# load the current $ra
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	jr $ra


DrawTurtle:
	lw $s2, 0($sp)  # load the y position value into $s2
	addi $sp, $sp, 4
	
	lw $s1, 0($sp)  # load the x position value into $s1
	addi $sp, $sp, 4
	
	addi $sp, $sp, -4
	sw $ra, 0($sp)  # store the current $ra value
	add $s0, $zero, $zero  # initialize the x offset to zero
DrawTurtleLoop:
	beq $s0, 504, EndShortLogLoop  # if the loop has iterated three times, end it
	
	# pass the sprite into the function
	addi $sp, $sp, -4 
	la $t1, turtleSprite
	sw $t1, 0($sp) 
	
	# pass the beginning pixel into the function
	add $t0, $zero, $s1  # load the x position of the log into $t0
	add $t0, $t0, $s0  # add the x offset to the x position
	
	blt $t0, 512, StoreFirstTurtlePixelPosition  # if the x position is greater than 512 due to the offset
	addi $t0, $t0, -512  # subtract 512 from it, that is, $t0 = $t0 % 512
StoreFirstTurtlePixelPosition:
	add $t1, $zero, $s2  # load the y position of the frog into $t1
	# calculate the beginning pixel of the sprite
	add $t1, $t1, $t0
	la $t2, 0($gp)  # load the display address
	add $t1, $t1, $t2  # add the pixel offset to the display address
	addi $sp, $sp, -4 
	sw $t1, 0($sp)  # store the beginning pixel in the stack
	
	# pass the area of the sprite
	addi $t0, $zero, 4096
	addi $sp, $sp, -4 
	sw $t0, 0($sp)
	
	# pass the row width of the sprite
	addu $t0, $zero, 128
	addi $sp, $sp, -4
	sw $t0, 0($sp)

	jal DrawSprite
	
	addi $s0, $s0, 168  # increment the x offset for the next log
	j DrawTurtleLoop
EndTurtleLoop:	
	# load the current $ra
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	jr $ra


DrawRow3Log:
	addi $sp, $sp, -4
	sw $ra, 0($sp)  # store the current $ra value
	add $s0, $zero, $zero  # initialize the x offset to zero
DrawRow3LogLoop:
	beq $s0, 512, EndRow3LogLoop  # if the loop has iterated three times, end it
	
	# pass the sprite into the function
	addi $sp, $sp, -4 
	la $t1, longLog
	sw $t1, 0($sp) 
	
	# pass the beginning pixel into the function
	addi $sp, $sp, -4 
	lw $t0, row3LogPosX  # load the x position of the log into $t0
	add $t0, $t0, $s0  # add the x offset to the x position
	
	blt $t0, 512, StoreFirstRow3LogPixelPosition  # if the x position is greater than 512 due to the offset
	addi $t0, $t0, -512  # subtract 512 from it, that is, $t0 = $t0 % 512
StoreFirstRow3LogPixelPosition:
	lw $t1, row3LogPosY  # load the y position of the frog into $t1
	# calculate the beginning pixel of the sprite
	add $t1, $t1, $t0
	la $t2, 0($gp)  # load the display address
	add $t1, $t1, $t2  # add the pixel offset to the display address

	sw $t1, 0($sp)  # store the beginning pixel in the stack
	
	# pass the area of the sprite
	addi $sp, $sp, -4 
	addi $t0, $zero, 4096
	sw $t0, 0($sp)
	
	# pass the row width of the sprite
	addi $sp, $sp, -4
	addu $t0, $zero, 192
	sw $t0, 0($sp)

	jal DrawSprite
	
	addi $s0, $s0, 256  # increment the x offset for the next log
	j DrawRow3LogLoop
EndRow3LogLoop:	
	# load the current $ra
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	jr $ra


DrawSprite:
	la $t0, 0($gp)  # $t0 stores the base address for display
	
	lw $a3, 0($sp)  # load the width of each row
	addi $sp, $sp, 4  # pop the stack
	
	lw $a2, 0($sp)  # load the size of the sprite
	addi $sp, $sp, 4  # pop the stack
	
	lw $a1 0($sp)   # load the beginning pixel position in $a1
	addi $sp, $sp, 4  # pop the stack
	
	lw $a0 0($sp)  # load the addres of the sprite in $a0
	addi $sp, $sp, 4  # pop the stack
	
	add $t2, $a1, $a2  # set $t2 as the end position
	addi $a1, $a1, -512 # decrement to get correct starting positing (because we increment at start of loop)
SpriteColumns:
	addi $a1, $a1, 512  # increment the starting position to the next row
	beq $a1, $t2, StopDrawingSprite
	add $t3, $a1, $zero  # set $t3 = $a1 the starting position
	add $t4, $t3, $a3  # set $t4 pixels away from $t3
	

SpriteRows:
	# $t3 is the current position of the pixel
	# $t4 is the desired end position of the pixel
	beq $t3, $t4, SpriteColumns  # if we reached end of row go to next column
	# store a secondary copy of the current position
	add $t7, $zero, $t3
	sub $t5, $t3, $t0  # get the current position in pixels
	sub $t6, $a1, $t0  # get current row start in pixels
	# round the current row start to the next multiple of 512 / column
	srl $t6, $t6, 9
	sll $t6, $t6, 9
	addi $t6, $t6, 512
	# if the current position goes to the next row then decrease $t7 by one row
	blt $t5, $t6, ContinueSpriteLoop
	addi $t7, $t7, -512
ContinueSpriteLoop:	
	lw $t1, 0($a0) # load the pixel color at this position
	sw $t1, 0($t7)  # paint the pixel at $t3
	addi $t3, $t3, 4  # increment $t2 to the next pixel position
	addi $a0, $a0, 4  # increment $a0 to next pixel color
	j SpriteRows
StopDrawingSprite:
	jr $ra


DrawFrog:
	la $t0, 0($gp)  # $t0 stores the base address for display
	la $a0, currentFrog # store the current frog sprite in $a0
	lhu $t5, frogX  # load the x position of the frog
	lhu $t6, frogY  # load the y position of the frog
	mul $t6, $t6, 128  # multiply y with 128 to get the number of rows in pixels
	add $t5, $t5, $t6 # add $t5 and $t6 together to get the position in total pixels (rows + shift right)
	add $t1, $t0, $t5 # the place to start drawing from
	addi $t2, $t1, 4096  # set $t2 as the end position
	addi $t1, $t1, -512 # decrement to get correct starting positing (because we increment at start of loop)
FrogColumns:
	addi $t1, $t1, 512  # increment the starting position to the next row
	beq $t1, $t2, StopDrawingFrog
	add $t3, $zero, $t1  # set $t3 = $t1
	addi $t4, $t3, 32  # set $t4 pixels away from $t3
FrogRows:
	# $t3 is the current position of the pixel
	# $t4 is the desired end position of the pixel
	beq $t3, $t4, FrogColumns  # if we reached end of row go to next column
	lw $t7, 0($a0) # load the pixel color at this position
	beq $t7, 0, NextFrogPixel  # if the pixel is black, don't draw it
	sw $t7, 0($t3)  # otherwise paint the pixel at $t3
NextFrogPixel:
	addi $t3, $t3, 4  # increment $t2 to the next pixel position
	addi $a0, $a0, 4  # increment $a0 to next pixel color
	j FrogRows
StopDrawingFrog:
	jr $ra
	
	
DrawSafeZone:
	la $t0, 0($gp) # $t0 stores the base address for display
	lw $a0, 0($sp) # load the position of the safe zone from the stack
	addi $sp, $sp, 4  # pop the stack
	add $t1, $t0, $a0  # define start location at (512, 504)
	addi $t2, $t1, 4096 # define end location at (512, 512)
	la $t3, safeZone  # store the address of the sprite colors in $t3
DrawSafeZoneLoop: 
	beq $t1, $t2, StopSafeZoneLoop # if end pixel is reached, stop drawing
	lw $t4, 0($t3)  # load the color from the sprite into $t4
	sw $t4, 0($t1)	# draw the pixel on the screen
	addi $t1, $t1, 4 #increment the position counter to the next pixel
	addi $t3, $t3, 4 # increment by 4 to get to the next pixel color
	j DrawSafeZoneLoop
StopSafeZoneLoop:
	jr $ra


DrawRoad:
	la $t0, 0($gp) # $t0 stores the base address for display
	lw $t5, safeZonePos  # load the start of the safe zone
	add $t1, $t0, $t5  # set $t1 to the start of the safe zone of the display
	addi $t2, $t0, 65536  # set $t2 to the endpoint of the display
	lw $t3, black  # load the color black into $t3
DrawRoadLoop: 
	beq $t1, $t2, ExitDrawRoadLoop  # if we draw every pixel up to and including the last one exit the loop
	sw $t3, 0($t1)  # draw the pixel at the location $t1
	addi $t1, $t1, 4  # increment $t1 to the next pixel
	j DrawRoadLoop  # restart the loop
ExitDrawRoadLoop:
	jr $ra 

	
DrawWater:
	la $t0, 0($gp) # $t0 stores the base address for display and the starting pixel i.e the top left corner
	lw $t5, safeZonePos  # load the start of the safe zone
	add $t2, $t0, $t5    # set $t2 to the endpoint of the display
	lw $t3, waterBlue  # load the color black into $t3
DrawWaterLoop:
	beq $t0, $t2, ExitDrawWaterLoop  # if we draw every pixel up to and including the last one exit the loop
	sw $t3, 0($t0)  # draw the pixel at the location $t1
	addi $t0, $t0, 4  # increment $t1 to the next pixel
	j DrawWaterLoop  # restart the loop
ExitDrawWaterLoop:
	jr $ra 
	
	
DrawGoalRegion:
	la $t0, 0($gp)
	addi $t1, $t0, 8192  # assign the start position
	addi $t2, $t1, 8192  # end position which is $128x16 pixels away from start position
	la $t3, goalRegion  # load the goal region sprite
DrawGoalRegionLoop:
	beq $t1, $t2, ExitDrawGoalRegionLoop
	lw $t4, 0($t3)  # load the color of the pixel
	sw $t4, 0($t1)  # draw the pixel at location $t1
	addi $t1, $t1, 4  # increment the location of the pixel by 1
	addi $t3, $t3, 4  # increment the color pointer by 1
	j DrawGoalRegionLoop
ExitDrawGoalRegionLoop:
	jr $ra
	
	
Sleep:
	li $v0, 32
	li $a0, 16  # sleep by 1/60 of a second
	syscall
	jr $ra
				
Exit:
	li $v0, 10 # terminate the program gracefully
	syscall
